import { Component } from "react";
import {Link} from 'react-router-dom';
import axios from "axios";

class Note extends Component {


    state = {
        note: [],
        loading:true,
    }
   
   async componentDidMount () {
       const response = await axios.get('http://127.0.0.1:8000/api/notes');
        console.log(response);
          if(response.data.status === 200) {
              this.setState({
                  note: response.data.note,
                  loading: false,
              });
          }

   }
   
   
    render() {
        
        const mystyle = {
            width: "18rem",
            flex: "1 1 auto",
            minheight: "1px",
            padding: "1.25rem"
          };

            var note_value = "";
            if(this.state.loading) {
                note_value = <h2>Loading ...</h2>
            } else {
                note_value = this.state.note.map( (item) => {
                    return (
                        <div>
                            {item.name}?
                        </div>
                    );
                });
            }

        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header">
                                    <h3>Notes
                                        <Link to={'add-note'} className="btn btn-primary btn-sm float-end">Add Note</Link> 
                                    </h3>
                                </div>                            
                            </div>
                        </div>
                    </div>
                </div> <br />
                {/* notes */}
                        <div className="container">
                            <div className="row">
                                <div className="col-sm-4">
                                    <div className="card" style={{mystyle}}>
                                        <div className="card-body">
                                            <h5 className="card-title">Card title</h5>
                                            <h6 className="card-subtitle mb-2 text-muted">Card subtitle</h6>
                                            <p className="card-text">Quick sample text to create the card title and make up the body of the card's content.</p>
                                            <a href="#" className="card-link">Edit</a>
                                            <a href="#" className="card-link">Delete</a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
            </div>
        )
    }
}

export default Note;
